import React, { Component } from 'react';
import { Button, Form, Grid, Header, Image, Message, Segment} from 'semantic-ui-react';
import { Link } from 'react-router-dom';
//import { logIn } from '../actions';


class LoginForm extends Component {
  constructor(props){
    super(props);
    this.state = {
      username: '',
      password: '',
      login: false,
    }
  }

  handleUsername = (e) => {
    this.setState({username: e.target.value});
  }

  handlePW = (e) => {
    this.setState({password: e.target.value});
  }

  handleClick = () => {
    this.setState({login: true});
  }

  render(){
    return(
      <div className='login-form'>
        <style>{`
          body > div,
          body > div > div,
          body > div > div > div.login-form {
            height: 100%;
          }
        `}</style>
        <Grid textAlign='center' style={{ height: '100%' }} verticalAlign='middle'>
          <Grid.Column style={{ maxWidth: 450 }}>
            <Header as='h2' color='teal' textAlign='center'>
              <Image src='/logo.png' /> Log-in to your account
            </Header>
            <Form size='large'>
              <Segment stacked>
                <Form.Input fluid icon='user' iconPosition='left' placeholder='Username' onChange={this.handleUsername}/>
                <Form.Input
                  fluid
                  icon='lock'
                  iconPosition='left'
                  placeholder='Password'
                  type='password'
                  onChange={this.handlePW}
                />

                <Button color='teal' fluid size='large' type='button' as={Link} to="/Home" onClick={this.handleClick}>
                  Login
                </Button>
              </Segment>
            </Form>
            <Message>
              New to us? <Link to="/Signup">Sign Up</Link>
            </Message>
          </Grid.Column>
        </Grid>
      </div>
    );
  }
}
/*
    <Form>
        <Form.Field>
          <label>Username</label>
          <input name='username' placeholder='Username' onChange={this.handleUsername} />
        </Form.Field>
        <Form.Field>
          <label>Password</label>
          <input name='password' placeholder='Password' onChange={this.handlePW}/>
        </Form.Field>
        <Button type='button' as={Link} to="/Home" onClick={this.handleClick}>Login</Button>
      </Form>
*/
export default LoginForm;
