import React, { Component } from 'react';
import { Button, Checkbox, Form } from 'semantic-ui-react';
import { NavLink } from 'react-router-dom';
import { connect } from 'react-redux';
import signUp from '../actions';

class SignupForm extends Component {
  // also need to input user information into database 
  constructor(props){
    super(props);
    this.state = {
      firstName: '',
      lastName: '',
      username: '',
      password: ''
    }
  }

  handleChange = (e) => {
    this.setState({
      [e.target.name]: e.target.value
    });
  }

  render(){
    return(
      <Form>
        <Form.Field>
          <label>First Name</label>
          <input name="firstName" placeholder='First Name' onChange={this.handleChange}/>
        </Form.Field>
        <Form.Field>
          <label>Last Name</label>
          <input name="lastName" placeholder='Last Name' onChange={this.handleChange}/>
        </Form.Field>
        <Form.Field>
          <label>Username</label>
          <input name="username" placeholder='Username' onChange={this.handleChange}/>
        </Form.Field>
        <Form.Field>
          <label>Password</label>
          <input name="password" placeholder='Password' onChange={this.handleChange}/>
        </Form.Field>
        <Form.Field>
          <Checkbox label='I agree to the Terms and Conditions' />
        </Form.Field>
        <Button type='submit' onClick={this.handleClick} as={NavLink} to="/Home">Submit</Button>
      </Form>
    );
  }
}

const mapStateToProps = state => {
  return {
    firstName: state.firstName,
    lastName: state.lastName,
    username: state.username,
    password: state.password
  }
}

const mapDispatchToProps = (dispatch, ownProps) => {
  return {
    onSignup: (first, last, username, password) => dispatch(signUp(first, last, username, password)),
  };
};

const SignupApp = connect(mapStateToProps,mapDispatchToProps)(SignupForm);

export default SignupApp;
