import React, { Component } from 'react';
import './Tetris.css';

const Tetris = require('react-tetris');

class TetrisGame extends Component {
  constructor(props){
    super(props);
    this.state = {
      points: 0
    }
  }

  handlePoints = (points) => {
    this.setState({points});
  }

  render(){
    console.log(this.state.points);
    return(
      <Tetris>
        {({
          HeldPiece,
          Gameboard,
          PieceQueue,
          points,
          linesCleared
        }) => {
          // Render it however you'd like
          return (
            <div>
              <HeldPiece />
              <div>
                <p onChange={this.handlePoints(points)}>Points: {points}</p>
                <p>Lines Cleared: {linesCleared}</p>
              </div>
              <Gameboard />
              <PieceQueue />
            </div>
          )
        }}
      </Tetris>
    );
  }
}


export default TetrisGame;
